package com.course.courseselection.controller;import com.course.courseselection.command.StudentCommand;import com.course.courseselection.command.StudentListResponse;import com.course.courseselection.command.UserCommand;import com.course.courseselection.service.AdminService;import com.course.courseselection.service.StudentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Pageable;import org.springframework.data.domain.Sort;import org.springframework.data.web.PageableDefault;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseStatus;import org.springframework.web.bind.annotation.RestController;import static org.springframework.http.HttpStatus.CREATED;import static org.springframework.web.bind.annotation.RequestMethod.*;@RestController@RequestMapping("/admin")public class AdminController {    @Autowired    private StudentService studentService;    @Autowired    private AdminService adminService;    @RequestMapping(method = POST)    @ResponseStatus(value = CREATED)    public void add(@RequestBody UserCommand command) {        adminService.add(command);    }    @RequestMapping(value = "/student", method = POST)    @ResponseStatus(value = CREATED)    public void add(@RequestBody StudentCommand request) {        adminService.add(request);    }    @RequestMapping(value = "/student", method = DELETE)    public void delete(StudentCommand command) {        adminService.delete(command);    }    @RequestMapping(value = "/student", method = GET)    public StudentListResponse query(@PageableDefault(value = 0, size = 20,            sort = "createdTime", direction = Sort.Direction.DESC) Pageable page) {        return studentService.query(page);    }}