package com.course.courseselection.controller;import com.course.courseselection.command.StudentListResponse;import com.course.courseselection.service.StudentService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Pageable;import org.springframework.data.domain.Sort;import org.springframework.data.web.PageableDefault;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseStatus;import org.springframework.web.bind.annotation.RestController;import static org.springframework.web.bind.annotation.RequestMethod.DELETE;import static org.springframework.web.bind.annotation.RequestMethod.GET;@RestController@RequestMapping("student")public class StudentController {    @Autowired    private StudentService studentService;    @RequestMapping(method = GET)    public StudentListResponse query(            @PageableDefault(value = 0, size = 20,                    sort = "createdTime", direction = Sort.Direction.DESC) Pageable page) {        return studentService.query(page);    }    @RequestMapping(method = DELETE)    @ResponseStatus(value = HttpStatus.OK)    public void remove(@RequestParam String studentId) {        studentService.remove(studentId);    }}