package com.course.courseselection.security;import com.course.courseselection.entity.TeacherData;import com.course.courseselection.repository.TeacherRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class TeacherUserDetailsServiceImp implements UserDetailsService {    @Autowired    private TeacherRepository teacherRepository;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        TeacherData teacherData = teacherRepository.findByName(username);        if (teacherData ==  null) {            throw new UsernameNotFoundException("teacher user name is not found");        }        return JwtUserFactory.create(teacherData);    }}