package com.course.courseselection.service;import com.course.courseselection.command.StudentInfoResponse;import com.course.courseselection.command.StudentListResponse;import com.course.courseselection.entity.StudentData;import com.course.courseselection.repository.StudentRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;import java.util.stream.Collectors;@Service@Transactionalpublic class StudentServiceImp implements StudentService {    @Autowired    private StudentRepository studentRepository;    @Override    public StudentListResponse query(Pageable page) {        Page<StudentData> all = studentRepository.findAll(page);        List<StudentInfoResponse> responseList = all.getContent().stream().map(studentData -> {            StudentInfoResponse studentInfoResponse = new StudentInfoResponse();            studentInfoResponse.setId(studentData.getId());            studentInfoResponse.setName(studentData.getName());            return studentInfoResponse;        }).collect(Collectors.toList());        StudentListResponse studentListResponse = new StudentListResponse();        studentListResponse.setStudents(responseList);        return studentListResponse;    }    @Override    public void remove(String studentId) {        StudentData entity = new StudentData();        entity.setId(studentId);        studentRepository.delete(entity);    }}